// 404, CMD not found error
exports.run = (client, msg) => {
  msg.channel.send("Sorry, this command doesn't exist yet\nFor a full list of commands please use " + client.config.prefix + "help");
}

// CMD Help Info
exports.help = {
  "name": "404",
  "desc": "Automatically runs when a user attempts to use a non-existent command",
  "use": "404"
}