// Several Admin CMDs for use only by me
exports.run = (client, msg) => {
  // console.log(msg.mentions);
  var first = msg.mentions.members.first();
  var perms = msg.member.permissions;
  var memId = msg.member.id;
  switch (client.args[0]) {
    case "kick":
      if (perms.has("KICK_MEMBERS")) {
        first.kick().then(() => {
          msg.channel.send("Succesfully kicked.");
        }).catch(() => {
          msg.channel.send("Failed.");
        });
      } else {
        msg.channel.send("You don't have the correct permissions.");
      }
      break;
    case "ban":
      if (perms.has("BAN_MEMBERS")) {
        first.ban().then(() => {
          msg.channel.send("Succesfully banned.");
        }).catch(() => {
          msg.channel.send("Failed.");
        });
      } else {
        msg.channel.send("You don't have the correct permissions.");
      }
      break;
    case "name":
      if (memId == 316481356060426241) {
        client.user.username = client.args.join();
      } else {
        msg.channel.send("You don't have the correct permissions.");
      }
      break;
  }
}   

// CMD Help Info
exports.help = {
  "name": "Admin",
  "desc": "Several Admin commands",
  "use": "admin [command] <args>"
}