// 
exports.run = (client, msg) => {
  guildConf = client.guildConfs.get(msg.guild.id);
  console.log(guildConf);
  switch (client.args[0]) {
    case "prefix":
      guildConf.prefix = client.args[1];
      break;
    case "welcomeChannel":
      guildConf.welcomeChannel = client.args[1];
      break;
    case "welcomeMsg":
      guildConf.welcomeMsg = client.args.shift().join(" ");
      break;
    client.guildConfs.set(msg.guild.id, guildConf);
    console.log(client.guildConfs); 
  }
}

// CMD Help Info
exports.help = {
  "name": "Config",
  "desc": "Sets the bot's server's settings.",
  "use": "config [prefix/welcomeChannel/welcomeMsg]"
}