//

const Discord = require("discord.js");

exports.run = (client, msg) => {
  var opts = msg.content.slice(client.config.prefix.length).trim().replace("poll", "").split("|");

  var embed = new Discord.RichEmbed()
                  .setTitle(msg.author)
                  .setFooter("Poll made with htoanyBot")
                  .setDescription(
                    opts[0] +
                    "\nVote Here" +
                    "\n1) " + opts[1] +
                    "\n2) " +opts[2]
                  );

  msg.channel.send(embed).then(nmsg => {
    nmsg.react("1⃣");
    nmsg.react("2⃣");
  });
  
}

// CMD Help Info
exports.help = {
  "name": "Poll",
  "desc": "A poll command that you vote with reactions",
  "use": "poll <description> | <option 1> | <option 2>"
}