// Reloads the CMD specified
exports.run = (client, msg) => {
  if (!client.args|| client.args.length < 1 || !client.config.cmds.includes(client.args[0])) {
    msg.reply("Try again with a command from " + client.config.prefix + "help");
    return;
  } else {
    delete require.cache[require.resolve("cmds/" + client.args[0] + ".js")];
    msg.channel.send("Reloaded the command " + client.args[0]);
  }
}

// CMD Help Info
exports.help = {
  "name": "Reload",
  "desc": "Reloads and deletes the cache for the specified command",
  "use": "reload [command]"
}