var types = ["play", "stream", "watch", "listen", "playing", "streaming", "watching", "listening"];

// Sets the Client status
exports.run = (client, msg) => {
  if (!client.args || client.args.length < 1) {
    client.user.setPresence({game: {name: "", type: "PLAYING"}});
  } else if (types.includes(client.args[0].toLowerCase())) {
    switch (client.args[0].toLowerCase()) {
      case "play":
        client.args.shift();
        client.user.setPresence({game: {name: client.args.join(" "), type: "PLAYING"}});
        break;
      case "playing":
        client.args.shift();
        client.user.setPresence({game: {name: client.args.join(" "), type: "PLAYING"}});
        break;
      case "stream":
        client.args.shift();
        client.user.setPresence({game: {name: client.args.join(" "), type: "STREAMING"}});
        break;
      case "streaming":
        client.args.shift();
        client.user.setPresence({game: {name: client.args.join(" "), type: "STREAMING"}});
        break;
      case "watch":
        client.args.shift();
        client.user.setPresence({game: {name: client.args.join(" "), type: "WATCHING"}});
        break;
      case "watching":
        client.args.shift();
        client.user.setPresence({game: {name: client.args.join(" "), type: "WATCHING"}});
        break;
      case "listen":
        client.args.shift();
        client.user.setPresence({game: {name: client.args.join(" "), type: "LISTENING"}});
        break;
      case "listening":
        client.args.shift();
        client.user.setPresence({game: {name: client.args.join(" "), type: "LISTENING"}});
        break;
      default:
        break;
    }
  } else {
    client.user.setPresence({game: {name: client.args.join(" "), type: "PLAYING"}});
  }
}

// CMD Help Info
exports.help = {
  "name": "Status",
  "desc": "Sets the status of the bot to the specified value",
  // "use": "status <play/stream/watch/listen> [nameOfGame]"
  "use": "status <play/watch/listen> [nameOfGame]"
}

